import React from 'react';
import {
    StyleSheet,
    Text,
    View,
    SafeAreaView,
    TextInput,
    Image,
    Switch,
    Button,
  } from "react-native";
import AppText from '../AppText';
function ErrorMessage({error,visible}) {
if (!visible || !error) return null;
    return (
        <AppText style={styles.error}>
            {error}
        </AppText>
    );
}

const styles = StyleSheet.create({
    error : {
        color : 'red'
    }
})

export default ErrorMessage;
